package java0514;

public class Ex6Prac {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 3; ++i) {
			System.out.printf("%d : Hello\n", i);
		}
		System.out.println("===========================");
		for (int i = 1; i <= 10; ++i) {
			System.out.printf("%d ", i);
		}
		System.out.println();
		for(int i = 11; i<=20; ++i) {
			System.out.printf("%d ", i);
		}
		System.out.println();
		for(int i = 0 ; i <=9; ++i) {
			System.out.printf("%d ",i);
		}
		System.out.println();
		for(int i = 2; i <=20; i+=2) {
			System.out.printf("%d ", i);
		}
		System.out.println();
		for(int i = 10; i > 0; --i) {
			System.out.printf("%d ", i);
		}
		System.out.println();
		System.out.println("==========================");
		for(int i = 1; i <=9; ++i) {
			System.out.printf("2 * %d = %d\n",i,2*i);
		}
		

	} // main

}
